// ================ IN PROGRESS ================
    function InProgress({userId}) {
        const [orders, setOrders] = useState([]);

        const fetchOrders = useCallback(() => {
            axios
                .get(`/personnel/orders/inprogress/${userId}`)
                .then(res => setOrders(res.data))
                .catch(() => setOrders([]));
        }, [userId]);

        useEffect(() => {
            fetchOrders();
        }, [fetchOrders]);

        const getNextStatus = (currentStatus) => {
            const flow = {
                'ACCEPTED': 'PENDING_COLLECTION',
                'PENDING_COLLECTION': 'WASHING',
                'WASHING': 'IRONING',
                'IRONING': 'DONE'
            };
            return flow[currentStatus];
        };

        // ðŸ”¥ UPDATED â€” Send timestamp with status change
        const handleUpdateStatus = async (orderId, currentStatus) => {
            const next = getNextStatus(currentStatus);
            if (!next) return;

            const timestamp = new Date().toISOString();

            try {
                await axios.put(`/personnel/orders/${orderId}/status`, {
                    status: next,
                    timestamp: timestamp
                });

                alert(`Status updated to ${next}!`);
                fetchOrders();
            } catch (err) {
                alert('Failed to update status');
            }
        };

        return (
            <div className="requests-list">
                <h3>In Progress</h3>
                {orders.length === 0 ? (
                    <p>No orders in progress</p>
                ) : (
                    orders.map(order => (
                        <div key={order.id} className="request-card two-column-card">

                            {/* LEFT SIDE */}
                            <div className="left-column">
                                <h4>Order #{order.id}</h4>
                                <p><strong>Status:</strong> {order.status}</p>
                                <p><strong>Student:</strong> {order.student.fullName}</p>
                                <p><strong>Phone:</strong> {order.student.phoneNumber}</p>
                                <p><strong>Total:</strong> â‚¹{order.totalPrice}</p>

                                <div className="request-items">
                                    <strong>Items:</strong>
                                    {order.items.map((item, idx) => (
                                        <div key={idx}>{item.itemType} x{item.quantity}</div>
                                    ))}
                                </div>

                                {/* ðŸ”¥ STATUS TIMELINE ADDED */}
                                <div className="status-history" style={{marginTop: "10px"}}>
                                    <strong>Status Timeline:</strong>
                                    {order.statusHistory?.length > 0 ? (
                                        order.statusHistory.map((h, idx) => (
                                            <div key={idx}>
                                                {h.status} â†’ <small>{new Date(h.timestamp).toLocaleString()}</small>
                                            </div>
                                        ))
                                    ) : (
                                        <p>No history available</p>
                                    )}
                                </div>

                            </div>

                            {/* RIGHT SIDE (PHOTOS) */}
                            <div className="right-column">
                                <OrderPhotoGallery orderId={order.id}/>
                            </div>

                            {/* ACTION BUTTON */}
                            <div className="request-actions-bottom">
                                <button
                                    className="btn-primary"
                                    onClick={() => handleUpdateStatus(order.id, order.status)}
                                >
                                    Update to {getNextStatus(order.status)}
                                </button>
                            </div>

                        </div>
                    ))
                )}
            </div>
        );
    }


// ================ COMPLETED ================
    function Completed({userId}) {
        const [orders, setOrders] = useState([]);
        const [stats, setStats] = useState({completedOrders: 0, totalEarnings: 0});

        useEffect(() => {
            axios
                .get(`/personnel/orders/completed/${userId}`)
                .then(res => setOrders(res.data))
                .catch(() => setOrders([]));

            axios
                .get(`/personnel/stats/${userId}`)
                .then(res => setStats(res.data))
                .catch(() => {
                });
        }, [userId]);

        return (
            <div className="requests-list">

                {/* TOP STATS */}
                <div className="stats-summary">
                    <div className="stat-card">
                        <h4>Completed Orders</h4>
                        <p className="stat-value">{stats.completedOrders}</p>
                    </div>
                    <div className="stat-card">
                        <h4>Total Earnings</h4>
                        <p className="stat-value">â‚¹{stats.totalEarnings}</p>
                    </div>
                </div>

                <h3>Completed Orders</h3>

                {orders.length === 0 ? (
                    <p>No completed orders yet</p>
                ) : (
                    orders.map(order => (
                        <div key={order.id} className="request-card two-column-card">

                            {/* LEFT SIDE */}
                            <div className="left-column">
                                <h4>Order #{order.id}</h4>
                                <p><strong>Student:</strong> {order.student.fullName}</p>
                                <p><strong>Total:</strong> â‚¹{order.totalPrice}</p>
                                <p><strong>Completed on:</strong> {order.updatedAt}</p>

                                <div className="request-items">
                                    <strong>Items:</strong>
                                    {order.items.map((item, idx) => (
                                        <div key={idx}>{item.itemType} x{item.quantity}</div>
                                    ))}
                                </div>

                                {/* ðŸ”¥ STATUS TIMELINE ADDED */}
                                <div className="status-history" style={{marginTop: "10px"}}>
                                    <strong>Status Timeline:</strong>
                                    {order.statusHistory?.length > 0 ? (
                                        order.statusHistory.map((h, idx) => (
                                            <div key={idx}>
                                                {h.status} â†’ <small>{new Date(h.timestamp).toLocaleString()}</small>
                                            </div>
                                        ))
                                    ) : (
                                        <p>No history available</p>
                                    )}
                                </div>

                            </div>

                            {/* RIGHT SIDE (PHOTOS) */}
                            <div className="right-column">
                                <OrderPhotoGallery orderId={order.id}/>
                            </div>

                            {/* ACTION (optional future btns) */}
                            <div className="request-actions-bottom">
                                <button className="btn-primary">
                                    View Details
                                </button>
                            </div>

                        </div>
                    ))
                )}
            </div>
        );
    }


// ================ ADMIN DASHBOARD ================
    function AdminDashboard() {
        const [stats, setStats] = useState(null);
        const [recentOrders, setRecentOrders] = useState([]);
        const navigate = useNavigate();

        useEffect(() => {
            const userData = JSON.parse(localStorage.getItem('user'));
            if (!userData || userData.role !== 'ADMIN') {
                navigate('/login');
                return;
            }

            axios.get('/admin/stats').then(res => setStats(res.data)).catch(() => {
            });
            axios.get('/admin/orders/recent').then(res => setRecentOrders(res.data)).catch(() => {
            });
        }, [navigate]);

        const handleLogout = () => {
            localStorage.removeItem('user');
            navigate('/');
        };

        if (!stats) return <div>Loading...</div>;

        return (
            <div className="dashboard">
                <nav className="dashboard-nav">
                    <div className="logo">ðŸ§º FreshFold Admin</div>
                    <button onClick={handleLogout} className="btn-secondary">Logout</button>
                </nav>

                <div className="admin-content">
                    <h2>Dashboard Overview</h2>

                    <div className="stats-grid">
                        <div className="stat-card">
                            <h3>Total Orders</h3>
                            <p className="stat-value">{stats.totalOrders}</p>
                        </div>
                        <div className="stat-card">
                            <h3>Completed</h3>
                            <p className="stat-value">{stats.completedOrders}</p>
                        </div>
                        <div className="stat-card">
                            <h3>Pending</h3>
                            <p className="stat-value">{stats.pendingOrders}</p>
                        </div>
                        <div className="stat-card">
                            <h3>Total Revenue</h3>
                            <p className="stat-value">â‚¹{stats.totalRevenue}</p>
                        </div>
                    </div>

                    <div className="reports-section">
                        <div className="report-box">
                            <h3>Hostel-wise Performance</h3>
                            <table>
                                <thead>
                                <tr>
                                    <th>Hostel</th>
                                    <th>Orders</th>
                                    <th>Revenue</th>
                                </tr>
                                </thead>
                                <tbody>
                                {stats.hostelStats?.map(h => (
                                    <tr key={h.hostelName}>
                                        <td>{h.hostelName}</td>
                                        <td>{h.orderCount}</td>
                                        <td>â‚¹{h.revenue}</td>
                                    </tr>
                                ))}
                                </tbody>
                            </table>
                        </div>

                        <div className="report-box">
                            <h3>Personnel Performance</h3>
                            <table>
                                <thead>
                                <tr>
                                    <th>Name</th>
                                    <th>Orders</th>
                                    <th>Earnings</th>
                                    <th>Rating</th>
                                </tr>
                                </thead>
                                <tbody>
                                {stats.personnelStats?.map(p => (
                                    <tr key={p.name}>
                                        <td>{p.name}</td>
                                        <td>{p.ordersCompleted}</td>
                                        <td>â‚¹{p.earnings}</td>
                                        <td>{'â˜…'.repeat(Math.floor(p.rating))}</td>
                                    </tr>
                                ))}
                                </tbody>
                            </table>
                        </div>
                    </div>

                    <div className="recent-orders">
                        <h3>Recent Orders</h3>
                        <table>
                            <thead>
                            <tr>
                                <th>ID</th>
                                <th>Student</th>
                                <th>Personnel</th>
                                <th>Status</th>
                                <th>Amount</th>
                            </tr>
                            </thead>
                            <tbody>
                            {recentOrders.map(o => (
                                <tr key={o.id}>
                                    <td>#{o.id}</td>
                                    <td>{o.student.fullName}</td>
                                    <td>{o.personnel?.fullName || 'N/A'}</td>
                                    <td>{o.status}</td>
                                    <td>â‚¹{o.totalPrice}</td>
                                </tr>
                            ))}
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        );
    }

// ================ MAIN APP ================
    function App() {
        return (
            <BrowserRouter>
                <Routes>
                    <Route path="/" element={<LandingPage/>}/>
                    <Route path="/login" element={<LoginPage/>}/>
                    <Route path="/signup" element={<SignupPage/>}/>
                    <Route path="/student/dashboard" element={<StudentDashboard/>}/>
                    <Route path="/personnel/dashboard" element={<PersonnelDashboard/>}/>
                    <Route path="/admin/dashboard" element={<AdminDashboard/>}/>
                    <Route path="*" element={<Navigate to="/"/>}/>
                </Routes>
            </BrowserRouter>
        );
    }

    export default App;
